import { useEffect, useState } from "react";
import axios from "axios";
import moment from "moment";
const Covics = () => {

    const [dataCovic, setdataCovic] = useState([]);
    //componentdidmount
    useEffect(async () => {
        let res = await axios.get('https://api.covid19api.com/country/vietnam?from=2021-10-01T00%3A00%3A00Z&to=2021-10-20T00%3A00%3A00Z');
        let data = res && res.data ? res.data : [];
        if (data && data.length > 0) {
            data.map(item => {
                item.Data = moment(item.Data).format('DD/MM/YYYY')
                return item;
            })
        }


    }, []);
    return (
        <table id="customers">
            <thead>
                <tr>
                    <th>Confirmed</th>
                    <th>Active</th>
                    <th>Deaths</th>
                    <th>Recovered</th>
                </tr>
            </thead>
            <tbody>
                {dataCovic && dataCovic.length > 0 &&
                    dataCovic.map(item => {
                        return (
                            <tr key={item.ID}>
                                <td>{item.Confirmed}</td>
                                <td>{item.Active}</td>
                                <td>{item.Deaths}</td>
                                <td>{item.Recovered}</td>
                            </tr>
                        )
                    })}

            </tbody>
        </table>
    )
}
export default Covics;
