import React from "react";
class Countdown extends React.Component {
    state = {
        count: 60
    }


    componentDidMount() {
        // setTimeout(() => {

        // }, 1000);

        setInterval(() => {
            this.setState({
                count: this.state.count - 1
            })
        }, 1000)
    }
    render() {
        return (
            <div>
                {this.state.count}
            </div>
        )
    }
}

export default Countdown;
