import useFetch from "../customize/fetch";
import './Blog.scss';
import { Link, useHistory } from "react-router-dom";
import Modal from 'react-bootstrap/Modal';
const Blog = () => {



    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);


    let history = useHistory();


    const { data: dataBlog, isLoading, isError }
        = useFetch(`https://jsonplaceholder.typicode.com/posts`, false);
    let newData = [];

    if (dataBlog && dataBlog.length > 0) {
        newData = dataBlog.slice(0, 10)

    }


    const handleAddNew = () => {
        history.push("/add-new-blog");
    }
    return (
        <>
            <div>
                <button className="btn-add-new" onClick={handleAddNew}>+ Add new blog</button>
            </div>
            <div className="blogs-container">
                {isLoading === false && newData && newData.length > 0 && newData.map(item => {
                    return (
                        <div className="single-post" key={item.id}>
                            <div className="title">{item.title}</div>
                            <div className="content">{item.body}</div>
                            <button><Link to={`/blog/${item.id}`}>View detail</Link></button>
                        </div>
                    )
                })}
                {isLoading === true &&
                    <div style={{ textAlign: 'center !important', with: '100%' }}>loading data... </div>
                }
            </div>
        </>
    )
}
export default Blog;
