          <span>-------------------</span>
          <NewCountdown onTimesup={onTimesup} />


          {/* <Todo
          todos={todos}
          title={'All todos'}
          handleDeleteTodo={handleDeleteTodo}
        />
        <Todo
          todos={todos.filter(item => item.type === 'lieu1')}
          title={`Lieu's todo`}
          handleDeleteTodo={handleDeleteTodo}
        />
        <input type="text" value={address} onChange={(event) => handleOnChangeInput(event)} />
        <button onClick={(event) => handleOnClick(event)}>Click me</button> */}
        </header>
        <Switch>
          <Route path="/">
            <Covics />
          </Route>
          <Route path="/users">
            {/* <Users /> */}
          </Route>
          <Route path="/">
            {/* <Home /> */}
          </Route>
        </Switch>
      </div>
    </Router>
  );
}

export default App;
