import { useEffect, useState } from "react";
import axios from "axios";
import moment from "moment";
const Covics = () => {

    const [dataCovic, setdataCovic] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [isError, setIsError] = useState(false);
    //componentdidmount
    useEffect(() => {
        try {

            async function fetchData() {
                let res = await axios.get('https://api.covid19api.com/country/vietnam?from=2021-10-01T00%3A00%3A00Z&to=2021-10-20T00%3A00%3A00Z');
                let data = res && res.data ? res.data : [];
                if (data && data.length > 0) {
                    data.map(item => {
                        item.Date = moment(item.Date).format('DD/MM/YYYY')
                        return item;
                    })
                    data = data.reverse();
                }
                setdataCovic(data);
                setIsLoading(false);
                setIsError(false);
            }
            fetchData();
        } catch (e) {
            setIsError(true);
            setIsLoading(false);
        }
    }, []);
    return (
        <table id="customers">
            <thead>
                <tr>
                    <th>Date</th>
                    <th>Confirmed</th>
                    <th>Active</th>
                    <th>Deaths</th>
                    <th>Recovered</th>
                </tr>
            </thead>
            <tbody>
                {isError === false && isLoading === false && dataCovic && dataCovic.length > 0 &&
                    dataCovic.map(item => {
                        return (
                            <tr key={item.ID}>
                                <td>{item.Date}</td>
                                <td>{item.Confirmed}</td>
                                <td>{item.Active}</td>
                                <td>{item.Deaths}</td>
                                <td>{item.Recovered}</td>
                            </tr>
                        )
                    })}
                {isLoading === true &&
                    <tr>
                        <td colSpan='5'>Loading....</td>
                    </tr>
                }
                {isError === true &&
                    <tr>
                        <td colSpan='5'>Something....</td>
                    </tr>
                }
            </tbody>
        </table>
    )
}
export default Covics;
