import logo from './logo.svg';
import './App.css';
import Nav from './views/Nav';
import { useState } from 'react';

const App = () => {

  let [name, setName] = useState('thanh lieu');
  let [address, setAddress] = useState('');
  let [todos, setTodos] = useState([
    { id: 'todo 1', name: 'xem phim' },
    { id: 'todo 2', name: 'học bài' },
    { id: 'todo 3', name: 'chơi game' }
  ]);
  const handleOnClick = (event) => {
    let todo = { id: 'abc', name: address }
    setTodos([...todos, todo]);
    setAddress();
  }
  const handleOnChangeInput = (event) => {
    setAddress(event.target.value);
  }
  return (

    <div className="App">
      <Nav />
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <h1>Hello world with React and {name}!</h1>
        <ul className='list-todos'>
          {
            todos.map(todo => {
              return (
                <li className='list-item' key={todo.id}>{todo.name}</li>
              )
            }
            )
          }


        </ul>
        <input type="text" value={address} onChange={(event) => handleOnChangeInput(event)} />
        <button onClick={(event) => handleOnClick(event)}>Click me</button>
      </header>
    </div>
  );
}

export default App;
